import "./ExpenseFilter.css";
import React from "react";

class ExpenseFilter extends React.Component {
  expenses = [];
  index = 0;
  options = []
  


  constructor(props) {
    super(props);

    this.state = {
      totalReactPackages: null,
    };
    this.componentDidMount();
  }
   yearChangeHandler = (event) => {
    
    props.getYearToFilter(event.target.value);
    this.expenses.forEach(element => {
      let year = new Date(element.date).getFullYear()
      
      if(!options.includes(year))
      {
        options.push(year);
      }
    });
    options =options.sort();
  };

  async componentDidMount() {
    // GET request using fetch with async/await
    
      const response = await fetch("http://localhost:5000/getAllExpenses");
      const data = await response.json();
      this.setState({ totalReactPackages: data.total });
      this.expenses = data.data;
  
  }

  render(){
  return (
    <div className="filter-cotainer">
      <label>Filter By Year</label>
      <select  onChange={yearChangeHandler} >
        
        {props.options.map((el) => {
          index++;
          return <option key={el}  selected={index === (props.options.length)} >{el}</option>;
        })}
      </select>
    </div>
  );}
};

export default ExpenseFilter;
