import "./ExpenseItem.css";

function ExpenseItem(props) {
    const elemnt_date = props.date.toLocaleDateString();
    const month = elemnt_date.toLocaleString('en-US',{month:'long'});
    const day = elemnt_date.toLocaleString('en-US',{day:'2-digit'});
    const year = props.date.getFullYear();
    
  if (!props) {
    return null;
  }
  return (
    <div className="expense-item" key={props.id}>
      <div>
        <div>{month}</div>
        <div>{year}</div>
        <div>{day}</div>
      </div>
      <div className="expense-item__description">
        <h2>{props.title}</h2>
        <div className="expense-item__price">{props.amount}</div>
      </div>
    </div>
  );
}

export default ExpenseItem;
