import "./ExpenseFilter.css";


const ExpenseFilter = (props) => {
  const yearChangeHandler = (event) => {
    try{
    props.getYearToFilter(event.target.value);}
    catch(ex){
      props.getYearToFilter(event);
    }
  };
  //yearChangeHandler("All Expenses");
  const initFilter=()=>{
    let input = document.getElementById("select_filter");

    const event = input.onChange;
    const tracker = input._valueTracker;
  
    if (tracker) {
  
      tracker.setValue("All Expenses");
  
    }
  
    input.dispatchEvent(event);
  

  }
 
  return (
    <div className="filter-cotainer">
      <label>Filter By Year</label>
      <select id="select_filter" onChange={yearChangeHandler}>
        <option key={0}>All Expenses</option>
        {props.options.map((el) => {
          return <option key={el}>{el}</option>;
        })}
      </select>
      <div>{initFilter}</div>
    </div>
    
  );
};
export default ExpenseFilter;
