import ExpenseItem from "../expense-item/ExpenseItem";
import "./Expenses.css";
import Card from "../expense-item/Card.js";
import ExpenseFilter from "../expenses-filter/ExpenseFilter";


const Expenses=(props)=> {
  let options = []
  let filtered_expenses = []
  props.expenses.forEach(element => {
    let year = new Date(element.date).getFullYear()
    
    if(!options.includes(year))
    {
      options.push(year);
    }
  });
  options =options.sort();

  const FilterYearHandler=(year)=>{
    //props.yearChanged(year)
    filtered_expenses =props.expenses.filter((el) => {
      let el_year = new Date(el.date).getFullYear();
      return el_year == year;
    });
  }
 
  return (
    

    <div><ExpenseFilter options={options} getYearToFilter={FilterYearHandler}></ExpenseFilter>
    <Card className="expenses-container">
      
      <ol>
        {props.expenses.map((el) => {
          return (
            <li key={el.id}>
              <ExpenseItem
                date={el.date}
                title={String(el.title).toLowerCase()}
                amount={el.amount}
              ></ExpenseItem>
            </li>
          );
        })}
      </ol>
    </Card>
    </div>
  );
}

export default Expenses;
