import './ExpenseForm.css';
import React,{ useState} from 'react';

const ExpenseForm =()=>
{
    const titleChangeHandler=(event)=>{
       setEnteredTitle(event.target.value);
      }
      const amountChangeHandler=(event)=>{
        setEnteredAmount(event.target.value);
       }
       const dateChangeHandler=(event)=>{
        setEnteredDate(event.target.value);
       }
      const [entered_title,setEnteredTitle]=useState('');
      const [entered_amount,setEnteredAmount]=useState('');
      const [entered_date,setEnteredDate]=useState('');
    
      return (
        <form>
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label>Title</label>
                    <input type='text' onChange={titleChangeHandler}></input>
                </div>
                <div className="new-expense__control">
                    <label>Amount</label>
                    <input onChange={amountChangeHandler} type='number' min='0.01' step='0.01'></input>
                </div>
                <div className="new-expense__control">
                    <label>Date</label>
                    <input onChange={dateChangeHandler} type='date' min='2019-01-01' max='2022-12-31'></input>
                </div>
            </div>
            <button type='submit'>Add Expense</button>
        </form>
    );
}

export default ExpenseForm;
