import "./ExpenseForm.css";
import React, { useState } from "react";

const ExpenseForm = () => {
  const titleChangeHandler = (event) => {
    setUserInput((prevState) => {
      return { ...userInput, enterdTitle: event.target.value };
    });
  };
  const amountChangeHandler = (event) => {
    setUserInput((prevState) => {
      return { ...userInput, enterdAmount: event.target.value };
    });
  };
  const dateChangeHandler = (event) => {
    setUserInput((prevState) => {
      
      return { ...userInput, enterdDate: event.target.value };
    });
  };
  const [enterdTitle, setEnterdTitle] = useState("")
  const [enterdAmount, setEnterdAmount] = useState("")
  const [enterdDate, setEnterdDate] = useState("")
  // const [userInput, setUserInput] = useState({
  //   enterdTitle: "",
  //   enterdAmount: "",
  //   enterdDate: "",
  // });
  const submitHandler = (event) => {
    
    event.prevetDefault();//prevents page reload
    const expenseData={
      title:enterdTitle,
      amount: enterdAmount,
      date:new Date(enterdDate)
    }
    
    console.log(expenseData)
  };
  return (
    <form onSubmit={(e) =>submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" value={enterdTitle} onChange={titleChangeHandler}></input>
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input value={enterdAmount}
            onChange={amountChangeHandler}
            type="number"
            min="0.01"
            step="0.01"
          ></input>
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input value={enterdDate}
            onChange={dateChangeHandler}
            type="date"
            min="2019-01-01"
            max="2022-12-31"
          ></input>
        </div>
      </div>
      <button type="submit">Add Expense</button>
    </form>
  );
};

export default ExpenseForm;
