import "./ExpenseFilter.css";
import React, { useState } from "react";

const ExpenseFilter = (props) => {

  const [selected_year, setYear] = useState({
    selectedYear:""
  })

 
  const yearChangeHandler = (event) => {
   
    setYear((prevState) => {
      return { ...selected_year,selectedYear: event.target.value };
    });
    
    props.getYearToFilter(selectedYear);
  };
  return (
    <div className="filter-cotainer">
      <select value={selectedYear} onChange={yearChangeHandler}>
        {props.options.map((el) => {
          return <option key={el} >{el}</option>;
        })}
      </select>
    </div>
  );
};
export default ExpenseFilter;
