import Expenses from "./components/expenses/Expenses.js";
import { Helmet } from "react-helmet";
import NewExpense from "./components/NewExpense/NewExpense";
import ExpenseFilter from "./components/expenses-filter/ExpenseFilter";
import React from "react";

class App extends React.Component {
  expenses = [];
  options = []

  options =options.sort();
  constructor(props) {
    super(props);

    this.state = {
      totalReactPackages: null,
    };
    this.componentDidMount();
  }
  addExpenseHandler = (expense) => {
    this.expenses.push(expense);
    this.setState({ totalReactPackages: this.expenses });
    this.addNewExpense(expense);
  };
  yearChangedHandler = (year) => {
    this.expenses =this.expenses.filter((el) => {
      let el_year = new Date(el.date).getFullYear();
      return el_year === year;
    });
    this.setState({ totalReactPackages: this.expenses });

  };

  async addNewExpense(expense)
  {
    const requestOptions = {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(expense)
      };
      fetch('http://localhost:5000/addNewExpense', requestOptions)
          .then(response => response.json())
          .then(data => this.setState({ totalReactPackages: data.total}));
  }
  async componentDidMount() {
    // GET request using fetch with async/await

      const response = await fetch("http://localhost:5000/getAllExpenses");
      const data = await response.json();
      this.setState({ totalReactPackages: data.total });
      this.expenses = data.data;
  this.expenses.forEach(element => {
    let year = new Date(element.date).getFullYear()

    if(!this.options.includes(year))
    {
      this.options.push(year);
    }
  });
  }


  render() {
    const { totalReactPackages } = this.state;
    return (
      <div>
        <Helmet>
          <title>Expenses Manager</title>
          <meta name="description" content="App Description" />
          <meta name="theme-color" content="#008f68" />
        </Helmet>
        <NewExpense onAddExpense={this.addExpenseHandler}></NewExpense>
        <ExpenseFilter options={options} getYearToFilter={this.yearChangedHandler}></ExpenseFilter>
        <Expenses
          expenses={this.expenses}
          yearChanged={this.yearChangedHandler}
        ></Expenses>
      </div>
    );
  }
}

export default App;
